import React from 'react';
import logo from './logo.svg';
import './App.css';
import 'tachyons';
import Searchbar from './Searchbar.js';


class App extends React.Component{
  constructor(){
    super();
    this.state = {
      number: 0,
      fact:''

    }
  }
  componentDidMount(){
    fetch(`http://numbersapi.com/${this.state.number}`)
    .then(response => {
        return response.text();
      })
    .then(data => 
      this.setState({
        fact: data
      }))
  }

  componentDidUpdate(prevProps) {
 //Typical usage, don't forget to compare the props
 if (this.props.number !== prevProps.number) {
   this.fetchData(this.props.number);
 }
}

  onSearch = (event)=> {
    event.preventDefault();
    const data = new FormData(event.target)
    this.setState({
      number: data.get('number')
  }
  )
  }

  render(){
    return (
    <div className= "tc">
      <h2 > Enter a number you wanna know a random trivia about: </h2>
      <Searchbar onSearch = {this.onSearch} />

      <h2> Here's your trivia </h2>
      <p> {this.state.fact}</p>

    </div>
  );
  }
}


export default App;
